import React, { Component } from 'react';
import axios from 'axios';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = { listing_type: 'listing_type', property_type: 'property_type', bedrooms: '' };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ listing_type: event.target.value, 
      property_type: event.target.value, 
      bedrooms: event.target.value 
    });
  }

  handleSubmit(event) {

    event.preventDefault();

    const { listing_type, property_type, bedrooms } = this.state;

    axios.post('http://localhost:4000/products', { listing_type, property_type, bedrooms })
      .then((result) => {
        console.log(result);
      });
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          Listing Type:

          <select value={this.state.listing_type} onChange={this.handleChange}>
            <option value="listing_type">Listing Type</option>
            <option value="rent">Rent</option>
            <option value="sell">Sell</option>
          </select>

        </label><br/>

        <label>
          Bedrooms:
        <input type="text" name="bedrooms" value={this.state.bedrooms} onChange={this.handleChange} placeholder={6} /><br/>
        </label>

        <label>
          Property Type:

          <select value={this.state.property_type} onChange={this.handleChange}>
            <option value="property_type">Property Type</option>
            <option value="Rent">Rent</option>
            <option value="Sell">Sell</option>
          </select>

        </label>

        <input type="submit" value="Submit" />
      </form>
    );
  }
}

export default App;
